package testingstuff;

public class SwappingValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int m=9,n=5,swap; //add values to the variables m and n. Add swap for later
System.out.println("Input : m=" +m +", n="+n ); //show the original values of m and n
swap = m; //gave the variable swap the value of m
m=n; //gave the variable m the value of n
n=swap; //gave the variable n the value of swap
System.out.println("Output : m=" +m +", n="+n); //reading the swapped values

	}

}
